scripts.test:generate:
npm run build && mkdir -p test-openapi test-output && cat > test-openapi/test.json << 'EOF'
{
  "openapi": "3.0.0",
  "info": {
    "title": "Test API",
    "version": "1.0.0"
  },
  "paths": {
    "/test": {
      "get": {
        "summary": "Test endpoint",
        "responses": {
          "200": {
            "description": "Success"
          }
        }
      }
    }
  }
}
EOF
node dist/cli.js generate-clients -i test-openapi -o test-output --client fetch
